      &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; (i = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;; target + i &amp;lt;= MAXNUMBER * m || target - i &amp;gt;= &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;; ++i) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; ((target + i &amp;lt;= MAXNUMBER * m &amp;amp;&amp;amp; dp[target + i]) || (target - i &amp;gt;= &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; &amp;amp;&amp;amp; target - i &amp;lt;= MAXNUMBER * m &amp;amp;&amp;amp; dp[target - i])) &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; i;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; i;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

&lt;p&gt;Êó∂Èó¥Â§çÊùÇÂ∫¶: O(m * max(mat[i][j]) * m * n) = 24,010,000,&lt;br&gt;Á©∫Èó¥Â§çÊùÇÂ∫¶: O(max(mat[i][j]) * m).&lt;/p&gt;
